export function AwaitingConnectionScene() {
  const {
    qrCodeContent,
    modalView
  } = useValues(widgetLogic);
  const {
    disableModal,
    setModalView
  } = useActions(widgetLogic);
  const {
    terminate
  } = useActions(verificationLogic);
  const {
    media
  } = useMedia();
  const [codeShown, setCodeShown] = useState(false); // Used for mobile

  const toggleCodeShown = useCallback(() => {
    setCodeShown(codeShown => !codeShown);
  }, []);
  const isVerificationFlow = useMemo(() => modalView === ModalView.VerificationFlow, [modalView]);
  interface CopyToClipboardProps {
    variant?: 'link';
    size?: 'sm' | 'xl';
    muted?: boolean;
    data?: string | null;
    color?: 'primary' | 'neutral';
  }

  function CopyToClipboard(props: CopyToClipboardProps) {
    const [isCopied, setIsCopied] = useState<boolean>(false);
    useEffect(() => {
      if (isCopied) {
        const timer = setTimeout(() => setIsCopied(false), 2000);
        return () => clearTimeout(timer);
      }

      return () => null;
    }, [isCopied]);
    const onClick = useCallback(() => {
      if (!props.data) {
        return;
      }

      navigator.clipboard.writeText(props.data).then(() => {
        setIsCopied(true);
      });
    }, [props.data]);
    return <Button variant={props.variant} size={props.size} color={props.color} onClick={onClick}>
        {isCopied && <IconCircleSuccess style={{
        marginRight: 4
      }} />}
        <span>{isCopied ? 'Copied!' : 'Copy QR code'}</span>
      </Button>;
  }

  return <Fragment>
      <DialogHeader extended={!isVerificationFlow}>
        {!isVerificationFlow && <DialogHeaderButton onClick={() => setModalView(ModalView.VerificationFlow)} bordered>
            <IconLeft />
          </DialogHeaderButton>}

        <DialogHeaderLogo />

        <DialogHeaderButton bordered onClick={() => {
        terminate();
        disableModal();
      }}>
          <IconClose />
        </DialogHeaderButton>
      </DialogHeader>

      <SMainContent>
        {isVerificationFlow && <Fragment>
            <SMainText>
              {
            /* TODO make Typography components be able to be rendered as different tags, not only div */
          }
              <SMainTextTitle variant="h1" color="gradient">
                Prove you haven’t done this before with World ID
              </SMainTextTitle>

              <SMainTextCaption variant="p1">
                Scan or copy this QR code with your phone’s camera or Worldcoin mobile app.
              </SMainTextCaption>
            </SMainText>

            <SMainCopy>
              {media === 'desktop' && <CopyToClipboard color="neutral" size="sm" data={qrCodeContent?.default} />}

              {media !== 'desktop' && !codeShown && <Button variant="link" color="default" size="xl" onClick={toggleCodeShown}>
                  Show QR code instead
                </Button>}

              {media !== 'desktop' && codeShown && <CopyToClipboard variant="link" color="primary" size="xl" data={qrCodeContent?.mobile || qrCodeContent?.default} />}
            </SMainCopy>

            <SMainCode>
              {media !== 'desktop' && !codeShown ? <WorldcoinApp /> : qrCodeContent ? <Qrcode data={qrCodeContent.default} /> : null}
            </SMainCode>

            <SMainCta>
              <ButtonLink color="gradient" size="xl" fullWidth href={(qrCodeContent?.mobile || qrCodeContent?.default) ?? 'https://worldcoin.org/verify'} target="_blank">
                Open Worldcoin app
              </ButtonLink>
            </SMainCta>
          </Fragment>}

        {!isVerificationFlow && <LearnMoreScene />}
      </SMainContent>
    </Fragment>;
}

import { useEffect, useState } from 'react'

export function useMedia() {
  const [media, setMedia] = useState<'mobile' | 'desktop'>('desktop')

  useEffect(() => {
    const mql = window.matchMedia('(max-width: 768px)')
    function handleChange(mql: MediaQueryList | MediaQueryListEvent) {
      if (mql.matches) {
        setMedia('mobile')
      } else {
        setMedia('desktop')
      }
    }
    handleChange(mql)
    mql.addEventListener('change', handleChange)
    return () => {
      mql.removeEventListener('change', handleChange)
    }
  }, [])

  return {
    media,
  }
}

import { useEffect, useRef } from 'react';
import { renderToString } from 'react-dom/server';
import { styled } from 'stitches';
import { WorldcoinLogomark } from 'assets/logos';
import QRCodeStyling from 'qr-code-styling-new';
const logoB64 = window.btoa(renderToString(<WorldcoinLogomark />));
const qrcode = new QRCodeStyling({
  width: 183,
  height: 183,
  type: 'svg',
  image: `data:image/svg+xml;base64,${logoB64}`,
  cornersSquareOptions: {
    type: 'extra-rounded'
  },
  cornersDotOptions: {
    type: 'dot'
  },
  dotsOptions: {
    color: 'var(--colors-qrcode)',
    type: 'extra-rounded'
  },
  backgroundOptions: {
    color: 'transparent'
  },
  imageOptions: {
    margin: 4,
    hideBackgroundDots: true
  }
});
const Root = styled('div', {
  position: 'relative',
  zIndex: '0',
  boxSizing: 'border-box',
  display: 'flex',
  width: '216px',
  height: '216px',
  alignItems: 'center',
  justifyContent: 'center',
  border: '1px solid $qrcodeBorder',
  borderRadius: '2px',
  '&::before, &::after': {
    content: '',
    display: 'block',
    position: 'absolute',
    zIndex: ' -1',
    background: '$background'
  },
  '&::before': {
    top: '24px',
    bottom: '24px',
    left: '-1px',
    right: '-1px'
  },
  '&::after': {
    top: '-1px',
    bottom: '-1px',
    left: '24px',
    right: '24px'
  },
  '& > *': {
    position: 'relative'
  }
});
interface QRRenderInterface {
  data: string;
}
export const Qrcode = ({
  data
}: QRRenderInterface): JSX.Element => {
  const ref = useRef<HTMLDivElement | null>(null);
  useEffect(() => {
    if (!ref.current) {
      return;
    }

    ref?.current && qrcode.append(ref.current);
  }, []);
  useEffect(() => {
    qrcode.update({
      data
    });
  }, [data]);
  return <Root ref={ref} />;
};

